import CharacterItem from './CharacterItem'

const CharacterGrid = ({ items, isLoading}) => {
  return isLoading ? (
    <h1>Loading...</h1>
  ) : (
    <section className='cards'>
        {items.map((items) => (
            <CharacterItem key={items.char_id} item={item}></CharacterItem>
        ))}
    </section>
  )
}

export default CharacterGrid
